javascript:
function revealFlag() {
    const splittedParts = window.location.pathname.split("/");
    const server_script_id = splittedParts[splittedParts.length-1];
    fetch(`/api/method/${server_script_id}`)
    .then((data)=>data.json())
    .then((data)=>{
        alert(data?.message?? "Something Go Wrong")
    })
}

window.addEventListener("DOMContentLoaded", function() {
    const splittedParts = window.location.pathname.split("/");
    const js_file_name = splittedParts[splittedParts.length-1];

    const script = document.createElement("script");
    script.src = `/private/files/${js_file_name}.js`;
    script.async = true;
    document.body.appendChild(script);
})

function revealFlag() {
    alert(window.getFlag());
}

css:
body {
    text-align: center;
    font-family: Arial, sans-serif;
}
.game-container {
    display: grid;
    grid-template-columns: repeat(3, 100px);
    grid-template-rows: repeat(3, 100px);
    gap: 5px;
    justify-content: center;
    margin-top: 20px;
}
.cell {
    width: 100px;
    height: 100px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 2em;
    border: 2px solid black;
    cursor: pointer;
}
.controls {
    margin-top: 20px;
}

