function sendCode(){
    frappe.call({
        "method": "ctf.api.stage.send_verification_code", 
        "args": {"email": document.getElementById("email").value},
        "freeze": true,
        "freeze_message": "Sending Code ..",
    })
}

function validateCode() {
    frappe.call({
        "method": "ctf.api.stage.validate_verification_code", 
        "args": {"code": document.getElementById("code").value,
        "freeze": true,
        "freeze_message": "Validating Code ..",
        }
    })
}
