function revealFlag() {
    const splittedParts = window.location.pathname.split("/");
    const server_script_id = splittedParts[splittedParts.length-1];
    fetch(`/api/method/${server_script_id}`)
    .then((data)=>data.json())
    .then((data)=>{
        alert(data?.message?? "Something Go Wrong")
    })
}

window.addEventListener("DOMContentLoaded", function() {
    const splittedParts = window.location.pathname.split("/");
    const js_file_name = splittedParts[splittedParts.length-1];

    const script = document.createElement("script");
    script.src = `/private/files/${js_file_name}.js`;
    script.async = true;
    document.body.appendChild(script);
})

function revealFlag() {
    alert(window.getFlag());
}
