javascript:
window.addEventListener("DOMContentLoaded", function() {
    const splittedParts = window.location.pathname.split("/");
    const js_file_name = splittedParts[splittedParts.length-1];

    const script = document.createElement("script");
    script.src = `/private/files/${js_file_name}.min.js`;
    script.async = true;
    document.body.appendChild(script);
})

function sendCode(){
    frappe.call({
        "method": "ctf.api.stage.send_verification_code", 
        "args": {"email": document.getElementById("email").value},
        "freeze": true,
        "freeze_message": "Sending Code ..",
    })
}

function validateCode() {
    frappe.call({
        "method": "ctf.api.stage.validate_verification_code", 
        "args": {"code": document.getElementById("code").value,
        "freeze": true,
        "freeze_message": "Validating Code ..",
        }
    })
}

function checkFlag() {
    frappe.call({
        "method": "ctf.api.stage.is_correct_flag", 
        "args": {"submitted_flag": document.getElementById("guessed_flag").value },
        "success": (e)=> frappe.msgprint(e?.message),
        "always": function(e){
            if(e.is_error) {
                frappe.msgprint("Something going wrong. Check error in console.")
                console.error(e?.message)
            }
        }
    })
}

function checkFlag() {
    const flag = document.getElementById("guessed_flag").value;

    fetch("/api/method/ctf.api.stage.check_flag", {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
            "Accept": "application/json",
            "X-Frappe-CSRF-Token": frappe.csrf_token
        },
        body: JSON.stringify({ flag })
    })
    .then(response => response.json())
    .then(data => frappe.msgprint(data?.message))
    .catch(error => {
        console.error("Error:", error);
        frappe.msgprint("An error occurred while checking the flag.");
    });
}
