function sendCode(){
    frappe.call({
        "method": "ctf.api.stage.send_verification_code", 
        "args": {"email": document.getElementById("email").value},
        "freeze": true,
        "freeze_message": "Sending Code ..",
    })
}

function validateCode() {
    frappe.call({
        "method": "ctf.api.stage.validate_verification_code", 
        "args": {"code": document.getElementById("code").value,
        "freeze": true,
        "freeze_message": "Validating Code ..",
        }
    })
}

function checkFlag() {
    const flag = document.getElementById("guessed_flag").value;

    fetch("/api/method/ctf.api.stage.check_flag", {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
            "Accept": "application/json",
            "X-Frappe-CSRF-Token": frappe.csrf_token
        },
        body: JSON.stringify({ flag })
    })
    .then(response => response.json())
    .then(data => frappe.msgprint(data?.message))
    .catch(error => {
        console.error("Error:", error);
        frappe.msgprint("An error occurred while checking the flag.");
    });
}

let template = `
{% if answer == super_secret %}
   Here is your flag: {{ flag }}
{% else %}
  <h4>Invalid secret</h4>
{% endif %}
`

function checkFlag() {
    const guessed_answer = document.getElementById("guessed_answer").value;

    fetch("/api/method/ctf.api.stage.retrieve_flag", {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
            "Accept": "application/json",
            "X-Frappe-CSRF-Token": frappe.csrf_token
        },
        body: JSON.stringify({
            template: template,
            answer: guessed_answer
        })
    })
    .then(response => response.json())
    .then(data => document.getElementById("content").innerHTML = data?.message??"")
    .catch(error => {
        console.error("Error:", error);
        frappe.msgprint("An error occurred while checking the flag.");
    });
}
