function revealFlag() {
    const splittedParts = window.location.pathname.split("/");
    const server_script_id = splittedParts[splittedParts.length-1];
    fetch(`/api/method/${server_script_id}`)
    .then((data)=>data.json())
    .then((data)=>{
        alert(data?.message?? "Something Go Wrong")
    })
}
